import  React, { useState } from  'react';
import { View, StyleSheet, TouchableOpacity, Image, Text, Dimensions } from 'react-native';


export default function Answer({answer_text, index, question}) {
    const [bk, setBK] = useState("#fff"); //Have its own state

    alert(JSON.stringify(answer_text))

    return (
        <TouchableOpacity style = {[test_page_style.slide_answer_container, {backgroundColor : bk}]}
            onPress = {()=>{
                if(question.answer_correct === answer_text){
                    setBK("green")
                } else {
                    setBK('red')
                }
            }}
        >
            <Text style = {test_page_style.slide_answer_text}>{answer_text}</Text>
        </TouchableOpacity>
    )
}


const test_page_style = StyleSheet.create({



    slide_answer_container : {
        borderRadius : 5, 
        borderColor : 'black',
        borderWidth : 1,
        width : 250,
        padding : 10,
        marginBottom : 15
    },

    slide_answer_text : {

    },




})
